package com.tarea;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*Ejercicio #1
Crear una clase (public static void main) con tres variables de tipo double e imprimir la suma, 
resta, multiplicación y división de las 3 variables. 
		 * */
		
		double a=1.1;
		double b=1.2;
		double c=1.3;
		
		double suma=a+b+c;
		double resta=a-b-c;
		double multiplicación = a*b*c;
		double división = a/b/c;
				
				
		System.out.println("Suma de las 3:"+suma);
		System.out.println("Resta de las 3: "+resta);
		System.out.println("Multiplicación de las 3: "+multiplicación);
		System.out.println("División de las 3: "+división);
	}

}
